                    {
                        p.MarkedAsDeleted = (bool)reader["MarkedAsDeleted"];
                    }
                    else
                    {
                        p.MarkedAsDeleted = false;
                    }
                }

                if (p.MarkedAsDeleted)
                {
                    p.RecordState = RecordState.DELETED;
