        public XDocument ConvertBillingInvoicesToXml(IEnumerable<Intravenous> items)
        {
            if (items == null)
                return null;
            var xmlDocument = new XDocument(new XDeclaration("1.0", "UTF-8", "yes"));
            var root = new XElement("IntravenousList");
            xmlDocument.Add(root);
